head.script:
function google_feedsearch_data(c, m, d) {
    if(Deki.hasValue(m.text)) {
        google.feeds.findFeeds(m.text, function(result) {
            if(result.error) {
                Deki.publish('debug', { text: result.error.message, info: result.error.code });
            } else {
                for(var i = 0; (i < result.entries.length) && (i < d.max); ++i) {
                    Deki.publish(d.channel, { label: result.entries[i].title, uri: result.entries[i].url });
                }
            }
        });
    }
}

tail.script:
var data = { channel: args.publish ?? "default", max: args.max ?? 4 };
Deki.subscribe(@id, null, google_feedsearch_data, data);
Deki.subscribe(args.subscribe, null, google_feedsearch_data, data);
Deki.publish(@id, { text: args.search });
