head.script:
function google_map_data(c, m, d) {
    var f = function(point) {
        if(!point) {
            Deki.publish('debug', { text: 'Could not find: ' + m.address });
        } else {
            d.map.setCenter(point);
            var marker = new GMarker(point);
            var text = '';
            if(Deki.hasValue(m.label)) text = '<strong>' + m.label + '</strong><br />';
            else if(Deki.hasValue(m.name)) text = '<strong>' + m.name + '</strong><br />';
            if(Deki.hasValue(m.info)) text += m.info + '<br />';
            if(Deki.hasValue(m.address)) text += '<a href=\'http://maps.google.com/maps?q=' + encodeURIComponent(m.address) + '&z=' + d.zoom + '\' target=\'_blank\'>' + m.address + '</a>';
            GEvent.addListener(marker, 'click', function() {
                marker.openInfoWindowHtml(text);
            });
            d.map.addOverlay(marker);
        }
    };
    if(Deki.hasValue(m.latitude) && Deki.hasValue(m.longitude)) {
        f(new GLatLng(m.latitude, m.longitude));
    } else if(Deki.hasValue(m.address)) {
        var geocoder = new GClientGeocoder();
        geocoder.getLatLng(m.address, f);
    }
}

tail.script:
var map = new GMap2(document.getElementById(@id));
map.addControl(new GSmallMapControl());
map.addControl(new GMapTypeControl());
map.setCenter(new GLatLng(32.7211, -117.174366), args.zoom ?? 14);
var data = { map: map, zoom: args.zoom ?? 14 };
Deki.subscribe(@id, null, google_map_data, data);
Deki.subscribe(args.subscribe, null, google_map_data, data);
Deki.publish(@id, { address: args.address, info: args.title ?? '' });
